import * as React from "react";
import * as commonStyles from "./common/styles/common.module.css";
import * as appStyles from "./App.css";
import * as classnames from "classnames/bind";
const cx = classnames.bind({ ...commonStyles, ...appStyles });

export const App = () => {
  const [initialPos, setInitialPos] = React.useState(null);
  const [initialSize, setInitialSize] = React.useState(null);

  const initial = (e) => {
    let resizable = document.getElementById("Resizable");
    setInitialPos(e.clientX);
    setInitialSize(resizable.offsetWidth);
  };

  const resize = (e) => {
    let resizable = document.getElementById("Resizable");
    resizable.style.width = `${parseInt(initialSize) + parseInt(e.clientX - initialPos)}px`;
  };
  return (
    <>
      <div className={cx("top-bar-div")}>
        <div>setting</div>
        <div>PlayerInfo</div>
        <div>View Select</div>
        <div>QuickLinks</div>
      </div>

      <div className={cx("main-container")}>
        <div className="Block">
          <div id="Resizable" />
          <div
            id="Draggable"
            draggable="true"
            onDragStart={initial}
            onDrag={resize}
          />
        </div>

      </div>
    </>
  );
};
